// Function to split text into semantic chunks
function splitIntoChunks(text, maxWords = 300) {
  const chunks = [];

  // Split by double newlines (paragraphs)
  const paragraphs = text.split(/\n\n+/).filter(p => p.trim().length > 0);

  let currentChunk = '';
  let chunkIndex = 0;

  for (const paragraph of paragraphs) {
    const trimmedParagraph = paragraph.trim();

    if (!trimmedParagraph) continue;

    // Count words in current chunk and paragraph
    const currentWords = currentChunk.split(/\s+/).filter(w => w.length > 0).length;
    const paragraphWords = trimmedParagraph.split(/\s+/).filter(w => w.length > 0).length;

    // If adding this paragraph would exceed max words
    if (currentWords + paragraphWords > maxWords && currentWords > 0) {
      // Save current chunk
      chunks.push({
        index: chunkIndex,
        content: currentChunk.trim(),
        wordCount: currentWords
      });
      chunkIndex++;
      currentChunk = '';
    }

    // If paragraph itself is larger than maxWords, split by sentences
    if (paragraphWords > maxWords) {
      // Split by sentences (., !, ?, or newlines)
      const sentences = trimmedParagraph.match(/[^.!?\n]+[.!?\n]+|[^.!?\n]+$/g) || [trimmedParagraph];

      for (const sentence of sentences) {
        const sentenceWords = sentence.trim().split(/\s+/).filter(w => w.length > 0).length;
        const currentChunkWords = currentChunk.split(/\s+/).filter(w => w.length > 0).length;

        if (currentChunkWords + sentenceWords > maxWords && currentChunkWords > 0) {
          chunks.push({
            index: chunkIndex,
            content: currentChunk.trim(),
            wordCount: currentChunkWords
          });
          chunkIndex++;
          currentChunk = sentence.trim();
        } else {
          currentChunk += (currentChunk ? ' ' : '') + sentence.trim();
        }
      }
    } else {
      // Add paragraph to current chunk
      currentChunk += (currentChunk ? '\n\n' : '') + trimmedParagraph;
    }
  }

  // Add the last chunk if it has content
  if (currentChunk.trim().length > 0) {
    const finalWords = currentChunk.split(/\s+/).filter(w => w.length > 0).length;
    chunks.push({
      index: chunkIndex,
      content: currentChunk.trim(),
      wordCount: finalWords
    });
  }

  return chunks;
}

