function calculateValueScore(article) {
    let score = 0;

    // 1. 互动指标（权重最高）
    score += (article.clapCount || 0) * 1;  // 每个赞1分
    score += (article.responseCount || 0) * 15;  // 付费文章的评论更有价值

    // 2. 付费内容基础加分
