            contentString = data[0];
        } else if (data.data) {
            contentString = data.data;
        }

        if (!contentString) {
            console.error('无法找到数据');
            return [];
        }

        const articles = [];
        const seenLinks = new Set();

        if (contentString.includes('<!doctype html') || contentString.includes('<html')) {
            console.log('检测到HTML格式');

            // 从JSON-LD中提取链接和元数据
            const jsonLdRegex = /<script[^>]*type=\"application\/ld\+json\"[^>]*>([\s\S]*?)<\/script>/g;
            let jsonLdMatch;

            while ((jsonLdMatch = jsonLdRegex.exec(contentString)) !== null) {
                try {
                    const jsonContent = jsonLdMatch[1];
                    const jsonData = JSON.parse(jsonContent);

                    if (jsonData.mainEntity && Array.isArray(jsonData.mainEntity)) {
                        jsonData.mainEntity.forEach(item => {
                            if (item.url && item.url.includes('medium.com')) {
                                const link = item.url.split('?')[0];

                                // 只添加付费文章（isAccessibleForFree: false）
                                if (!seenLinks.has(link) && item.isAccessibleForFree === false) {
                                    seenLinks.add(link);
                                    articles.push({
                                        link: link,
                                        headline: item.headline || item.name || '',
                                        description: item.description || '',
                                        pubDate: item.datePublished || null,
                                        isAccessibleForFree: false,
                                        isPaidContent: true
                                    });
                                }
                            }
                        });
                    }
                } catch (e) {
                    // 跳过解析失败的JSON
                }
            }

            // 从Apollo State中提取详细互动数据
            const apolloStateRegex = /window\.__APOLLO_STATE__\s*=\s*({[\s\S]*?});/;
            const apolloMatch = apolloStateRegex.exec(contentString);

            if (apolloMatch) {
                try {
                    const apolloState = JSON.parse(apolloMatch[1]);

                    // 遍历Apollo State查找Post数据
                    Object.keys(apolloState).forEach(key => {
                        if (key.startsWith('Post:')) {
                            const post = apolloState[key];
                            if (post.mediumUrl && post.isLocked === true) {
                                const link = post.mediumUrl.split('?')[0];
                                let existingArticle = articles.find(a => a.link === link);

                                // 如果文章还不在列表中，但是付费的，就添加进去
                                if (!existingArticle && !seenLinks.has(link)) {
                                    seenLinks.add(link);
                                    existingArticle = {
                                        link: link,
                                        headline: post.title || '',
                                        pubDate: post.firstPublishedAt ? new Date(post.firstPublishedAt).toISOString() : null,
                                        isAccessibleForFree: false,
                                        isPaidContent: true
                                    };
                                    articles.push(existingArticle);
                                }

                                if (existingArticle) {
                                    // 添加互动数据
                                    existingArticle.clapCount = post.clapCount || 0;
                                    existingArticle.responseCount = post.postResponses?.count || 0;
                                    existingArticle.readingTime = post.readingTime || 0;
                                    existingArticle.isLocked = true;
                                    existingArticle.visibility = post.visibility || 'LOCKED';

                                    // 计算价值分数
                                    existingArticle.valueScore = calculateValueScore(existingArticle);
                                }
                            }
                        }
                    });
                } catch (e) {
                    console.log('Apollo State解析失败，使用基础排序');
                }
            }
        }

        // 按价值分数排序（高到低）
        articles.sort((a, b) => (b.valueScore || 0) - (a.valueScore || 0));

        console.log(`共找到 ${articles.length} 篇付费文章`);

        return articles;
    } catch (error) {
        console.error('解析错误:', error.message);
        return [];
    }
}

// 计算文章价值分数（针对付费文章优化）
function calculateValueScore(article) {
    let score = 0;

    // 1. 互动指标（权重最高）
    score += (article.clapCount || 0) * 1;  // 每个赞1分
    score += (article.responseCount || 0) * 15;  // 付费文章的评论更有价值

    // 2. 付费内容基础加分
    score += 100;  // 付费文章基础分

    // 3. 阅读时长合理性（5-20分钟最佳，付费文章通常更长）
    if (article.readingTime >= 5 && article.readingTime <= 20) {
        score += 50;
    } else if (article.readingTime > 20 && article.readingTime <= 30) {
        score += 30;
    }

    // 4. 时效性（最近3天的文章加分）
    if (article.pubDate) {
        const daysAgo = (Date.now() - new Date(article.pubDate).getTime()) / (1000 * 60 * 60 * 24);
        if (daysAgo <= 3) {
            score += 150;  // 最新付费文章
        } else if (daysAgo <= 7) {
            score += 80;
        } else if (daysAgo <= 14) {
            score += 40;
        }
    }

    // 5. 高互动门槛（付费文章有高互动说明质量极高）
    if (article.clapCount > 1000) {
        score += 200;
    }
    if (article.responseCount > 30) {
        score += 150;
    }

    return score;
}

