// 合并内容
let mergedMarkdown = '';

dataArray.forEach((item, index) => {
  const englishContent = item.content.trim();
  const chineseContent = item.output.trim();

  // 清理中文内容：移除 markdown 标记和多余字符
  const cleanedChinese = chineseContent
    // .replace(/\\n/g, '')           // 移除 \n 字符串
    // .replace(/\n/g, '')            // 移除实际换行符
    // .replace(/\\r/g, '')           // 移除 \r 字符串
