    // .replace(/\\t/g, '')           // 移除 \t 字符串
    // .replace(/\t/g, '')            // 移除实际制表符
    // .replace(/[#*_`\[\]()]/g, '')  // 移除常见 markdown 符号
    .replace(/\s+/g, ' ')          // 合并多个空格
